// 这是测试页面
<template>
  <div class="my-div">
    <img :class="{ 'arrowTransform': !flag, 'arrowTransformReturn': flag}" src="arrow.png" alt="" @click="flag = !flag">

  </div>
</template>

<script>
export default {
  flag : false,
}
</script>

<style  scoped>
.my-div {  
  width: 100%;
  height: 100%;
  background-color: white; /* 将背景颜色设置为白色 */  
}
.arrowTransform{
    transition: 0.2s;
    transform-origin: center;
    transform: rotateZ(180deg);
  }
  .arrowTransformReturn{
    transition: 0.2s;
    transform-origin: center;
    transform: rotateZ(0deg);
  }

</style>
