// 这是测试页面
<template>
  <div class="bk">
    <i
      class="el-icon-arrow-left"
      :class="{ 'arrowTransform': !flag, 'arrowTransformReturn': flag}"
      alt
      @click="flag = !flag"
    ></i>
  </div>
</template>

<script>
export default {
  data() {
    return {
      flag: false
    };
  }
};
</script>

<style  scoped>
.bk{
  width: 100%;
  height: 100%;
  background-color: white; /* 将背景颜色设置为白色 */
}
.arrowTransform {
  transition: 0.2s;
  transform-origin: center;
  transform: rotateZ(-90deg);
}
.arrowTransformReturn {
  transition: 0.2s;
  transform-origin: center;
  transform: rotateZ(0deg);
}
</style>
